raw.css:
/* container styles */
#container {
	margin: 0;
    height: 100vh;
    background: rgb(36, 40, 49);
    color: white;
    display: grid;
    place-content: center; /* optional */
}

header {
	display: flex;

    .activator {
        padding: 1em;
        border-radius: 100%;
        cursor: pointer;
    }
    .activator:hover {
        background: rgb(30,33,41);
    }
}


img {
	width: 100%;
    max-width: 45px;
}

ul {
	list-style-type: none;
    margin: 0;
    padding: 0;
}

#custom-nav {
	background: rgb(41,46,56);
    border-radius: 0 5em 5em 0;
    padding: .5em;
    clip-path: ellipse(50% 50% at -50% 50%);

    ul {
    	display: flex;

        li a {
        	/* needed for padding to work right */
        	display: block;
            padding: .5em;
            margin: 0 .5em;
            border-radius: 50%;
            /* transition: background-color .3s; */

            img {
            	opacity: 0;
                transform: translateX(-10px);
            }
        }
        li a:hover {
        	background: rgb(50,56,68);
        }
    }
}

